argList => {
    const gf = eval(globals.gf);
    const max = 99999999999999999999;
    const min = 10000000000000000000;
    const getRandom = length => (Math.random() * (max - min) + min).toString().substring(1,length + 1);

    const materialNumber = getRandom(18);
    const unitPrice      = getRandom(2) + '.00';
    const standardPrice  = getRandom(2) + '.00';
    const materialDescription       = `Nestle N${getRandom(5)} product from ${getRandom(5)} section`;
    const secondMaterialDescription = `Nestle N${getRandom(5)} product from ${getRandom(5)} section`;

    const baseUrl = pm.environment.get("ip");
    const url = `${baseUrl}products`;
    const createProductPayload = eval(pm.variables.get("capturePostProductPayload"));
    const accessToken = pm.globals.get("accessToken");
    const customHeader = pm.globals.get("customHeader");

    const postRequest = {
        url: url,
        method: 'POST',
        header: {
            'content-type': 'application/json',
            'Authorization': 'Bearer ' + accessToken,
            'User-Agent': customHeader
        },
        body: {
            mode: 'raw',
            raw: JSON.stringify(createProductPayload(
                standardPrice,
                unitPrice,
                materialNumber,
                materialDescription,
                secondMaterialDescription
            ))
        }
    };
    pm.sendRequest(postRequest, (err, res) => {
        if (err) {
            console.warn(err)
        }
        pm.globals.set('standardPrice', standardPrice)
        pm.globals.set('unitPrice', unitPrice)
        pm.globals.set('materialNumber', materialNumber)
        pm.globals.set('materialDescription', materialDescription)
        pm.globals.set('secondMaterialDescription', secondMaterialDescription)
        gf.chainRequests(gf, argList)
    });        
};

