        { number: l5, level: "05" },
        { number: l6, level: "06" },
        { number: l7, level: "00" }
    ];
    let newArr = []
    for (let i = 0; i < hierarchies.length; i++) {
        let obj = {}
        obj['number'] = []
        if ([i - 1] >= 0)
            obj['number'].push(hierarchies[i - 1].number)
        obj['number'].push(hierarchies[i].number)
        obj['level'] = hierarchies[i].level
        newArr.push(obj)
    }
    newArr.forEach(({ number, level }) => {
        if (number.length < 1) { number.unshift(Math.random().toString().slice(2, 12)) }
        let anterior
